impl Spaceship { fn get_name(&self) -> &str { //用借贷运算符将字符串转换为字符串片断 &self.name } fn add_fuel(&mut self, gallons: f64) { self.propellant += gallons; } // 这是一个关联函数 // 与结构数据类型相关联 // 与方法类似，但没有&self引用 // 用于与该结构相关的一般函数。 // 而不是一个特定的实例 //（就像面向对象中的类静态方法）。 // 通常用于构建结构体的实例 fn new(name: &str) -> Spaceship { Spaceship { name: String::from(name), crew: 11, // 默认值，所有实例都一样 propellant: 0.0 // 默认值，所有实例都一样 } }
}
fn main() { let mut spaceship1 = Spaceship { name: String::from("spaceship1"), crew: 123, propellant: 1234567.654, }; //访问字段值 println!("spaceship1 has {} members.", spaceship1.crew); spaceship1.crew = 99; println!( "After attack, spaceship1 has now {} members.", spaceship1.crew ); println!("spaceship1 is {:?}", spaceship1); // 默认情况下，结构数据被存储在 STACK 上。 // 如果结构包含HEAP存储的数据（如字符串）。 // 指针存储在STACK上，数据存储在HEAP上。 // 从其他实例的字段中创建结构实例 //（就像JS中的spread operator，但是是两个点而不是三个点）。 // 这就是所谓的结构更新语法 // 它允许创建新的实例 // 通过复制现有实例的字段值来创建新实例 //（除了在新实例中明确设置的字段）。 let spaceship2 = Spaceship { name: String::from("Galactos"), ..spaceship1 }; println!("spaceship2 is {:?} members.", spaceship2); // 在修改复制的结构实例时 // 不会影响复制后的新实例。 //（与保留的JavaScript引用不同。 // 当用数组传播对象时......) spaceship1.name = String::from("Battlestar"); spaceship1.crew = 78; assert_eq!(spaceship2.crew, 99); println!("spaceship1 is {:?}", spaceship1); println!("spaceship2 is {:?}", spaceship2); // 要复制所有字段，甚至是基于堆的字段。 // 你需要创建一个结构实例的副本。 // 否则，当使用结构更新语法时，会转移基于堆的数据的所有权 // 这意味着你不能再使用复制的实例中的相关字段。 let mut spaceship3 = Spaceship { //需要手动实现克隆特性 //在结构定义中（见上文）。 ..spaceship1.clone() }; println!("spaceship3 is {:?}", spaceship3); let name = spaceship3.get_name(); println!("name is {}", name); println!("spaceship3 propellant is {}", spaceship3.propellant); spaceship3.add_fuel(4567.113); println!( "After going to space refuel station, spaceship3 propellant is now {}", spaceship3.propellant ); //要调用一个指定的函数，请使用路径操作符（::）。 let spaceship4 = Spaceship::new("Serenity"); assert_eq!(spaceship4.crew, 11);
}

